document.addEventListener('DOMContentLoaded',function(){const videoElements=document.querySelectorAll('[data-video-id]');if(videoElements){videoElements.forEach(function(video){video.addEventListener('click',function(){console.log('play video');const videoId=this.getAttribute('data-video-id');const videoURL='https://www.youtube.com/embed/'+videoId+'?autoplay=1';const videoPlayer=this.parentElement.parentElement.nextElementSibling;video.parentElement.parentElement.style.opacity='0';videoPlayer.setAttribute('src',videoURL);});});}});